mapper.select:
SELECT * FROM ( SELECT TICKETINGNO, TRAINNAME, TRAINNO, VEHICLEKNDNM, DEPPLACENAME, DEPPLANDTIME, ARRPLACENAME, ARRPLANDTIME, RESERVATIONNO, RATE, SEATDIVISION, SEAT, PASSENGERTYPE, TICKETINGDATE, TICKETINGETC, ROW_NUMBER() OVER(ORDER BY TO_DATE(DEPPLANDTIME,'YYYY-MM-DD hh24:MI') DESC) AS RN FROM TICKETING_TB WHERE USERNO = #{ticketingVO.userNo}
AND TICKETINGDATE BETWEEN TO_DATE(#{ticketingVO.ticketingDate},'YY/MM/DD') AND ROUND(TO_DATE(#{ticketingVO.ticketingDate},'YY/MM/DD')+1)
) WHERE RN>=#{paging.startPage} AND RN <= #{paging.endPage}

mapper.delete:

UPDATE  TICKETING_TB
SET TICKETINGETC = #{ticketingETC}
WHERE USERNO = #{userNo} AND DEPPLACENAME = #{depPlaceName} AND ARRPLACENAME = #{arrPlaceName}
	AND DEPPLANDTIME = #{depPlandTime} AND ARRPLANDTIME = #{arrPlandTime}
	AND SEATDIVISION = #{seatDivision} AND SEAT = #{seat}
